import styled from "styled-components";

export const StyledNavbar = styled.nav`
  position: absolute;
  display: flex;
  justify-content: space-between;
  /* align-items: center; */
  /* width: 50%; */

  .navigation {
    align-items: center;
    /* justify-content: center; */
    display: flex;
    gap: 3rem;

    * {
      cursor: pointer;
      transition: text-decoration 200ms ease-in;
      /* padding-bottom: 4px; */
      &:hover{
        text-decoration: underline 3px solid #0000ff;
        text-underline-offset: 4px
      }
    }
  }

  h3 {
    font-size: 1.25rem;
  }

  .hamburguer {
    display: none;
  }

  @media (max-width: 47em) {
    .navigation {
      position: fixed;
      inset: 0 0 0 30%;
      flex-direction: column;
      padding-top: 7rem;
      gap: 4rem;

      background: hsl(0 0% 40% / 0.1);
      backdrop-filter: blur(1rem);
      .styled-button {
        width: 60%;
      }

      transform: translateX(100%);
      transition: transform 350ms ease-out
    }
    .true {
      transform: translateX(0)
    }


    .hamburguer {
    display: block;
  }

    
  }
`;
