import styled from "styled-components";

export const HamburguerMenu = styled.button`
position: absolute;
width: 2.5rem;
aspect-ratio: 1;
background-color: transparent;
border: none;
top: 2rem;
right: 2rem;
z-index: 9999;

span{
    display: block;
    position: relative;
}

span,
span::before,
span::after {
    width: 2em;
    height: 3px;
    background-color: white;

    transition: transform 250ms ease-in-out;
}

span::before,
span::after {
    content: '';
    position: absolute;
    left: 0;
}

span::before{
    bottom: 6px;
}
span::after{
    top: 6px;
}
`
