import styled from "styled-components";

export const StyledNavbar = styled.nav`
  display: flex;
  justify-content:space-between;
  /* position: sticky; */

  .navigation{
    align-items: center;
    display:flex;
    gap: 3rem;

    * {
        cursor: pointer;
    }
  }

  h3{
    font-size:1.25rem;
  }

  @media (max-width: 45em) {
    .navigation {
      position: fixed;
      inset: 0 0 0 30%;
      background: steelblue;
      flex-direction: column;
      padding-top: 2rem;
    }
  }
`
