import styled from "styled-components";

export const StyledNavbar = styled.nav`
  display: flex;
  justify-content: space-between;
  /* position: sticky; */

  .navigation {
    align-items: center;
    /* justify-content: center; */
    display: flex;
    gap: 3rem;

    * {
      cursor: pointer;
    }
  }

  h3 {
    font-size: 1.25rem;
  }

  .hamburguer {
    display: none;
  }

  @media (max-width: 45em) {
    .navigation {
      position: fixed;
      inset: 0 0 0 30%;
      flex-direction: column;
      padding-top: 2rem;

      background: hsl(0 0% 40% / 0.1);
      backdrop-filter: blur(1rem);
      .styled-button {
        width: 60%;
      }
    }
    .hamburguer {
    display: block;
  }

    
  }
`;
