import styled from "styled-components";

export const StyledMainPage = styled.div`
    height: calc(100vh - 90px);
    position: relative;
    .vertical-line {
        border-left: 1px solid #fff;
        height:35vh;
        position: absolute;
        bottom: 0;
    }
    a {
        position: absolute;
        transform: rotate(90deg);
        font-size: 1.125rem;
        bottom: 20px;
    }
`
