import styled from "styled-components";

export const StyledMainPage = styled.div`
    height: calc(100vh - 90px);
    position: relative;
    .vertical-line {
        border-left: 1px solid #fff;
        height:45vh;
        position: absolute;
        bottom: 0;
    }
`
