import styled from "styled-components";

export const HamburguerMenu = styled.button`
position: absolute;
width: 2.5rem;
aspect-ratio: 1;
background-color: transparent;
top: 2rem;
right: 2rem;
z-index: 9999;

span{
    display: block;
    position: relative;
}

span,
span::before,
span::after {
    width: 2em;
    height: 3px;
    background-color: white;

}

`
