import styled from "styled-components";

interface Props {
    background: string
}
// #0000FF, #FF00FF
// #202020, #202020

export const StyledButton = styled.button<Props>`
  ${(props) => {
    const { background } = props;
    return `
    color: white;
font-size: 16px;
background: 
    linear-gradient(${background}, ${background}}) padding-box,
    linear-gradient(to right, #0000FF, #FF00FF) border-box;
  border-radius: 10px;
  border: 2px solid transparent;
  `;
  }}
`;

export const ResumeButton = styled(StyledButton)`
  padding: 1em;
`;
